import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {  Car, FuelTypeMap } from './Models/Car';
import { NavLink } from 'react-router-dom';
import { Button } from 'semantic-ui-react';

export default function CarList() {
  const [cars, setCars] = useState<Car[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    setLoading(true); // Dodajemy setLoading na początku
    
    axios.get<Car[]>('https://localhost:7072/api/cars/')
        .then((response) => {
            setCars(response.data);
        })
        .catch(() => {
            setError('Error fetching cars');
        })
        .finally(() => {
            setLoading(false);
        });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;



  return (
    <div>
      <h1>Car List</h1>
      <ul>
        {cars.map(car => (
          <li key={car.id}>
            <h2>{car.brand} {car.model} 
              <Button as={NavLink} to={`/cars/${car.id}`} style={{
        margin: '10px',               // Margines wokół przycisku
        backgroundColor: '#4CAF50',   // Kolor tła przycisku
        color: 'white',                // Kolor tekstu
        borderRadius: '20px',          // Zaokrąglone rogi
        padding: '10px 20px',          // Padding wewnątrz przycisku
        fontWeight: 'bold',            // Pogrubienie tekstu
        transition: 'background-color 0.3s ease, transform 0.3s ease', 
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',  // Cień dla przycisku
      }}>Szczegóły</Button>
              <Button as={NavLink} to={`/edit/${car.id}`}>Edytuj</Button>
            </h2>
            <p>Doors: {car.doorsNumber}</p>
            <p>Luggage Capacity: {car.luggageCapacity} liters</p>
            <p>Engine Capacity: {car.engineCapacity} cc</p>
            <p>Fuel Type: {FuelTypeMap[parseInt(car.fuelType)] || 'Unknown'}</p>
            <p>Production Date: {new Date(car.productionDate).toLocaleDateString()}</p>
            <p>Fuel Consumption: {car.carFuelConsumption} L/100km</p>
            <p>Body Type: {car.bodyType}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}
