import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Car, FuelType, BodyType } from './Models/Car';
import axios from 'axios';

const FuelTypeFromNumber = (num: number): FuelType =>
  Object.values(FuelType)[num];

const BodyTypeFromNumber = (num: number): BodyType =>
  Object.values(BodyType)[num];

const FuelTypeToNumber = (s: FuelType): number =>
  Object.values(FuelType).indexOf(s);

const BodyTypeToNumber = (s: BodyType): number =>
  Object.values(BodyType).indexOf(s);

export default function CarEdit() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [car, setCar] = useState<Car | null>(null);

  useEffect(() => {
    const fetchCarById = async () => {
      try {
        const response = await axios.get(
          `https://localhost:7135/api/Cars/${id}`
        );
        const data = response.data;

        setCar({
          ...data,
          fuelType: FuelTypeFromNumber(data.fuelType),
          bodyType: BodyTypeFromNumber(data.bodyType),
        });
      } catch {
        alert("Error fetching car");
      }
    };
    fetchCarById();
  }, [id]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
  if (!car) return;

  const { name, value } = e.target;

  // dla input[type=number] – usuwamy wiodące zera
  if (["doorsNumber", "luggageCapacity", "engineCapacity", "carFuelConsumption"].includes(name)) {
    const cleaned = value.replace(/^0+(?=\d)/, "");

    setCar({
      ...car,
      [name]: cleaned === "" ? 0 : Number(cleaned)
    });

    return;
  }

  setCar({
    ...car,
    [name]: value
  });
};

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!car) return;

    const carToSend = {
      ...car,
      fuelType: FuelTypeToNumber(car.fuelType),
      bodyType: BodyTypeToNumber(car.bodyType)
    };

    try {
      await axios.put(`https://localhost:7135/api/Cars/${id}`, carToSend);
      navigate("/");
    } catch {
      alert("Error saving changes");
    }
  };

  if (!car) return <div>Loading...</div>;

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        maxWidth: "400px",
        display: "flex",
        flexDirection: "column",
        gap: "15px",
        padding: "20px"
      }}
    >
      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>Brand:</label>
        <input
          type="text"
          name="brand"
          value={car.brand}
          onChange={handleChange}
        />
      </div>

      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>Model:</label>
        <input
          type="text"
          name="model"
          value={car.model}
          onChange={handleChange}
        />
      </div>

      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>Doors Number:</label>
        <input
          type="number"
          name="doorsNumber"
          value={car.doorsNumber}
          onChange={handleChange}
        />
      </div>

      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>Luggage Capacity:</label>
        <input
          type="number"
          name="luggageCapacity"
          value={car.luggageCapacity}
          onChange={handleChange}
        />
      </div>

      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>Engine Capacity:</label>
        <input
          type="number"
          name="engineCapacity"
          value={car.engineCapacity}
          onChange={handleChange}
        />
      </div>

      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>Fuel Type:</label>
        <select name="fuelType" value={car.fuelType} onChange={handleChange}>
          {Object.values(FuelType).map(fuel => (
            <option key={fuel} value={fuel}>{fuel}</option>
          ))}
        </select>
      </div>

      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>Production Date:</label>
        <input
          type="date"
          name="productionDate"
          value={car.productionDate.slice(0, 10)}
          onChange={handleChange}
        />
      </div>

      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>Fuel Consumption:</label>
        <input
          type="number"
          name="carFuelConsumption"
          value={car.carFuelConsumption}
          onChange={handleChange}
        />
      </div>

      <div style={{ display: "flex", flexDirection: "column" }}>
        <label>Body Type:</label>
        <select name="bodyType" value={car.bodyType} onChange={handleChange}>
          {Object.values(BodyType).map(body => (
            <option key={body} value={body}>{body}</option>
          ))}
        </select>
      </div>

      <button
        type="submit"
        style={{
          padding: "10px",
          background: "black",
          color: "white",
          fontWeight: "bold",
          borderRadius: "8px"
        }}
      >
        Save
      </button>
    </form>
  );
}
